import React, { useState } from 'react';
import './login.css';
import LoginImage from '../../assets/login.png';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Fonction pour valider les entrées utilisateur
  const validateInputs = () => {
    if (!username.trim() || !password.trim()) {
      setErrorMessage('Username and password are required.');
      return false;
    }
    setErrorMessage('');
    return true;
  };

  // Fonction pour gérer la soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault(); // Empêche le rechargement de la page

    if (!validateInputs()) return;

    setIsLoading(true); // Active l'état de chargement
    const loginData = {
      username,
      password,
    };

    try {
      const response = await fetch('http://localhost:8082/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      const responseText = await response.text();

      if (response.ok) {
        // Vérification si la réponse est un JSON ou une chaîne brute
        let result;
        try {
          result = JSON.parse(responseText); // Tentative d'analyse JSON
        } catch {
          result = { token: responseText }; // Si ce n'est pas un JSON, l'utiliser comme une chaîne brute
        }

        if (result.token) {
          alert('Connexion réussie');
          localStorage.setItem('token', result.token); // Stocke le token JWT
          window.location.href = '/home'; // Redirection après connexion réussie
        } else {
          setErrorMessage('Token non fourni par le serveur.');
        }
      } else {
        // Gestion des erreurs venant du serveur
        let errorResponse;
        try {
          errorResponse = JSON.parse(responseText);
        } catch {
          errorResponse = { message: responseText };
        }
        setErrorMessage(errorResponse.message || 'Erreur de connexion.');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion:', error);
      setErrorMessage('Une erreur s\'est produite. Veuillez réessayer.');
    } finally {
      setIsLoading(false); // Désactive l'état de chargement
    }
  };

  return (
    <div className="login-container">
      {/* Section gauche : Formulaire de connexion */}
      <div className="login-left">
        <h2>Login</h2>
        {errorMessage && (
          <div className="error-message" style={{ color: 'red', marginBottom: '15px' }}>
            {errorMessage}
          </div>
        )}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          <button
            type="submit"
            className="login-button"
            disabled={isLoading} // Désactive le bouton pendant le chargement
          >
            {isLoading ? 'Logging in...' : 'Login'}
          </button>
        </form>
        <div className="options">
          <a href="/forgot-password">Forgot password?</a>
        </div>
        <div className="signup">
          <p>
            Don't have an account? <a href="/signup">Sign up</a>
          </p>
        </div>
      </div>

      {/* Section droite : Illustration */}
      <div className="login-right">
        <img src={LoginImage} alt="Login Illustration" className="login-image" />
      </div>
    </div>
  );
}

export default Login;
